import React, { Component } from 'react';

import volunteer1 from '../images/volunteer1.jpg';

import { List, ListItem, ListItemText } from '@material-ui/core';

class HowToHelp extends Component {
    handleItemClicked = (selectedIndex) => {
        var nextPath = '';

        switch (selectedIndex) {
            case 0:
                nextPath = '/';
                break;
            case 1:
                nextPath = '/about-our-site';
                break;
            case 2:
                nextPath = '/the-facts';
                break;
            case 3:
                nextPath = '/how-to-help';
                break;
            default:
                break;
        }

        this.props.history.push(nextPath);
    }

    render() {
        return (
            <div>
                <nav className="navbar navbar-expand-lg">
                    <div className="navbar-brand">Hope in Seattle</div>
                    <List className="navbar-nav ml-auto">
                        <ListItem button className="nav-item" onClick={() => this.handleItemClicked(0)}>
                            <ListItemText primary="Home" />
                        </ListItem>
                        <ListItem button className="nav-item" onClick={() => this.handleItemClicked(1)}>
                            <ListItemText primary="About Our Site" />
                        </ListItem>
                        <ListItem button className="nav-item" onClick={() => this.handleItemClicked(2)}>
                            <ListItemText primary="The Facts" />
                        </ListItem>
                        <ListItem button className="nav-item" onClick={() => this.handleItemClicked(3)}>
                            <ListItemText primary="How to Help" />
                        </ListItem>
                    </List>
                </nav>

                <main className="container-fluid">
                    <section className="row showcase-img">
                        <img className="container-fluid p-0 height opacity60" src={volunteer1} alt="Volunteers" />
                        <div className="centered">
                            <h1 className="display-1">How To Help</h1>
                            <form class="form-inline my-2 my-lg-0">
                                <input class="form-control mr-sm-2" type="search" placeholder="Search" aria-label="Search" />
                            </form>
                        </div>
                    </section>

                    <section>
                        <h2 className="display-2 py-5">Statistics</h2>
                        <div className="container">
                            <div className="row">
                                <div className="col-12">
                                    <div className="thumbnail-img">
                                        <img className="img img-responsive full-width" src={statistics1} alt="Bar Graph" />
                                    </div>
                                </div>
                                <div className="col-12">
                                    <p className="text-left">On any given night, there are over 600,000 homeless people in the U.S., according to the U.S. Department of Housing and Urban Development (HUD). In 2017, Seattle had the third-largest homeless population and continues to see a rise in homeless people on the streets.</p>
                                    <p className="text-left">Jenny Durkan, the mayor of Seattle, states that, "Seattle's homelessness crisis has been years in the making, and its roots run deep, touching racial inequity, economic disparities, mental health treatment, rising housing costs, mental health, addiction, and so much more. We have a responsibility to be honest that this crisis won't go away overnight. Lasting, meaningful progress will take years. But we still must act - and are acting - to improve life in Seattle."</p>
                                    <p className="text-left"></p>
                                </div>
                            </div>
                        </div>
                    </section>
                </main>

                <nav className="navbar navbar-expand-lg"></nav>
            </div>
        );
    }
}

export default HowToHelp;
