import React, { Component } from 'react';

import homeless1 from '../images/homeless1.jpg';
import statistics1 from '../images/statistics1.jpg';

class TheFacts extends Component {
    handleItemClicked = (selectedIndex) => {
        var nextPath = '';

        switch (selectedIndex) {
            case 0:
                nextPath = '/';
                break;
            case 1:
                nextPath = '/about-our-site';
                break;
            case 2:
                nextPath = '/the-facts';
                break;
            case 3:
                nextPath = '/how-to-help';
                break;
            default:
                break;
        }

        this.props.history.push(nextPath);
    }

    render() {
        return (
            <div>
                <nav className="navbar navbar-expand-lg">
                    <div className="navbar-brand">Hope in Seattle</div>
                    <ul className="navbar-nav ml-auto">
                        <li className="nav-item" onClick={() => this.handleItemClicked(0)}>Home</li>
                        <li className="nav-item" onClick={() => this.handleItemClicked(1)}>About Our Site</li>
                        <li className="nav-item" onClick={() => this.handleItemClicked(2)}>The Facts</li>
                        <li className="nav-item" onClick={() => this.handleItemClicked(3)}>How to Help</li>
                    </ul>
                </nav>

                <main className="container-fluid">
                    <section className="row showcase-img">
                        <img className="container-fluid p-0 height opacity60" src={homeless1} alt="Homeless" />
                        <div className="centered">
                            <h1 className="display-1">The Facts</h1>
                        </div>
                    </section>

                    <section>
                        <h2 className="display-2 py-5">Statistics</h2>
                        <div className="container">
                            <div className="row">
                                <div className="col">
                                    <div className="thumbnail-img">
                                        <img className="img img-responsive full-width" src={statistics1} alt="Bar Graph" />
                                    </div>
                                </div>
                                <div className="col">
                                    <p className="text-left">On any given night, there are over 600,000 homeless people in the U.S., according to the US Department of Housing and Urban Development (HUD).</p>
                                    <p className="text-left">"Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum."</p>
                                </div>
                            </div>
                        </div>
                    </section>
                </main>

                <nav className="navbar navbar-expand-lg"></nav>
            </div>
        );
    }
}

export default TheFacts;
